/*
node1:
127.0.0.1:1231-1233 - CONSENSUS
127.0.0.1:1234 - proxy
node2:
127.0.0.2:1231-1233 - CONSENSUS
127.0.0.2:1234 - proxy
*/
let baseNodeID = 1112,
    stepNodeID = 1;
let baseNodePort = 1231,
    stepNodePort = 100;
let baseProxyPort = 15000,
    stepProxyPort = 100; // stepProxyPort MUST BE non-zero
let baseProxyPortS = 15010,
    stepProxyPortS = 100; // stepProxyPort MUST BE non-zero
let baseProxyPortWS = 15020,
    stepProxyPortWS = 100; // stepProxyPortWS MUST BE non-zero
let baseProxyPortWSS = 15030,
    stepProxyPortWSS = 100; // stepProxyPortWS MUST BE non-zero
function calc_node_id( idx ) { // idx is 0-based
    return baseNodeID + idx * stepNodeID;
}

function calc_node_ip4( idx ) { // idx is 0-based
    var idx1 = idx + 1; // idx1 is 1-based
    return "127.0.0." + idx1;
}

function calc_node_port( idx ) { // idx is 0-based
    return baseNodePort + idx * stepNodePort;
}

function calc_proxy_port( idx ) { // idx is 0-based
    return baseProxyPort + idx * stepProxyPort;
}

function calc_proxy_port_s( idx ) { // idx is 0-based
    return baseProxyPortS + idx * stepProxyPortS;
}

function calc_proxy_port_ws( idx ) { // idx is 0-based
    return baseProxyPortWS + idx * stepProxyPortWS;
}

function calc_proxy_port_wss( idx ) { // idx is 0-based
    return baseProxyPortWSS + idx * stepProxyPortWSS;
}

