function create_node_config( idx ) { // idx is 0-based
    var idx1 = idx + 1; // idx1 is 1-based
    var jo = JSON.parse( JSON.stringify( joConfigurationTemplate ) ),
        i;
    jo.skaleConfig.nodeInfo = {
        "nodeName": "Node" + idx1,
        "nodeID": calc_node_id( idx ),
        "bindIP": calc_node_ip4( idx ) // "127.0.0.1"
            ,
        "basePort": calc_node_port( idx ),
        "bindIP6": "::1",
        "basePort6": calc_node_port( idx ),
        "logLevel": "trace",
        "logLevelProposal": "trace",
        "adminOrigins": [ "*" ],
        "emptyBlockIntervalMs": 1000, // 3600000000
        "ipc": false,
        //
        "ipcpath": "./ipcx", // normal
        "db-path": "./node", // normal
        // "ipcpath": "/Gig/btrfs_root/nodes/" + idx + "/ipcx", // btrfs
        // "db-path": "/Gig/btrfs_root/nodes/" + idx + "/node", // btrfs
        //
        "httpRpcPort":  calc_proxy_port( idx ),
        "httpsRpcPort": calc_proxy_port_s( idx ),
        "wsRpcPort":    calc_proxy_port_ws( idx ),
        "wssRpcPort":   calc_proxy_port_wss( idx ),
        "httpRpcPort6":  calc_proxy_port( idx ),
        "httpsRpcPort6": calc_proxy_port_s( idx ),
        "wsRpcPort6":    calc_proxy_port_ws( idx ) + 20, //  + 20 is temp workaround
        "wssRpcPort6":   calc_proxy_port_wss( idx ) + 20, //  + 20 is temp workaround
        "acceptors": 1,
        "max-connections": 0,
        "max-http-queues": 0,
        "ws-mode": "simple",
        "ws-log": "none",
        "web3-trace": true,
        "enable-debug-behavior-apis": true,
        "unsafe-transactions": false,
        "aa": "always",
        "web3-shutdown": true,
        "imaMainNet": "http://127.0.0.1:8545",
        "imaMessageProxySChain": "0x4cC0e8b2347172011798e6C6c196360F9f4ad113",
        "imaMessageProxyMainNet": "0x1830e6cac0b1D202E0D329874d39680D68729Aa9",
        "imaCallerAddressSChain": "0x66c5a87f4a49DD75e970055A265E8dd5C3F8f852",
        "imaCallerAddressMainNet": "0x7aa5e36aa15e93d10f4f26357c30f052dacdde5f"
};
    if( cntNodes in g_mapImaWalletConfigurations ) {
        var arrImaWalletConfigurations = g_mapImaWalletConfigurations[ cntNodes ];
        jo.skaleConfig.nodeInfo.wallets = {};
        jo.skaleConfig.nodeInfo.wallets.ima = arrImaWalletConfigurations[ idx ];
    }
    jo.skaleConfig.sChain.schainID = 1; //533; // idx1;
    var arrNodes = [];
    for ( i = 0; i < cntNodes; ++i ) {
        arrNodes.push( {
            "nodeID": calc_node_id( i ),
            "ip": calc_node_ip4( i ) // "127.0.0.1"
                ,
            "basePort": calc_node_port( i ),
            "ip6": "::1",
            "basePort6": calc_node_port( idx ),
            "schainIndex": i + 1, // schainIndex is 1-based
            "httpRpcPort":  calc_proxy_port( i ),
            "httpsRpcPort": calc_proxy_port_s( i ),
            "wsRpcPort":    calc_proxy_port_ws( i ),
            "wssRpcPort":   calc_proxy_port_wss( i ),
            "httpRpcPort6":  calc_proxy_port( i ),
            "httpsRpcPort6": calc_proxy_port_s( i ),
            "wsRpcPort6":    calc_proxy_port_ws( i ),
            "wssRpcPort6":   calc_proxy_port_wss( i )
        } );
    }
    jo.skaleConfig.sChain.nodes = arrNodes;
    return jo;
}

