let strTemplatesDirectory = __dirname + path.sep + "templates"

function copy_template_file( idx, strTemplateFileName, strNodeFolder, isExecutable ) {
    if ( isExecutable === undefined )
        isExecutable = true;
    var srcPath = strTemplatesDirectory + path.sep + strTemplateFileName;
    var dstPath = strNodeFolder + path.sep + strTemplateFileName;
    var s = fs.readFileSync( srcPath, "utf8" );
    //console.log(s);
    var i, cnt = 20;
    for( i = 0; i < cnt; ++ i ) s = s.replace( "%%NODE_IDX%%", "" + idx );
    for( i = 0; i < cnt; ++ i ) s = s.replace( "%%NODE_ID%%", "" + calc_node_id( idx ) );
    for( i = 0; i < cnt; ++ i ) s = s.replace( "%%NODE_IP4%%", "" + calc_node_ip4( idx ) );
    for( i = 0; i < cnt; ++ i ) s = s.replace( "%%NODE_PORT%%", "" + calc_node_port( idx ) );
    for( i = 0; i < cnt; ++ i ) s = s.replace( "%%PROXY_PORT%%", "" + calc_proxy_port( idx ) );
    for( i = 0; i < cnt; ++ i ) s = s.replace( "%%PROXY_PORT_S%%", "" + calc_proxy_port_s( idx ) );
    for( i = 0; i < cnt; ++ i ) s = s.replace( "%%PROXY_PORT_WS%%", "" + calc_proxy_port_ws( idx ) );
    for( i = 0; i < cnt; ++ i ) s = s.replace( "%%PROXY_PORT_WSS%%", "" + calc_proxy_port_wss( idx ) );
    fs.writeFileSync( dstPath, s );
    if ( isExecutable )
        fs.chmodSync( dstPath, 0777 );
}

