var strContentRunSH = "#!/bin/bash\necho inside run.sh the SKALED=$SKALED\n";
var strContentShutdownSH = "#!/bin/bash\n";
var nsp = 1;
var strNSP = nestedSpace( nsp )
for ( idxNode = 0; idxNode < cntNodes; ++idxNode ) {
    log.write( cc.debug( "Initializing node " ) + cc.bright( idxNode ) + cc.debug( "..." ) + "\n" );
    //
    // create config.json
    //
    var strNodeFolder = __dirname + path.sep + "node_" + zeroPad( idxNode, 2 );
    log.write( strNSP + cc.debug( "Directory is " ) + cc.info( strNodeFolder ) + "\n" );
    if ( !fs.existsSync( strNodeFolder ) ) {
        fs.mkdirSync( strNodeFolder ); // , { recursive: true, mode: 0o777 }
        fs.chmodSync( strNodeFolder, 0777 );
    }
    var joConfiguration = create_node_config( idxNode );
    //log.write( strNSP + cc.debug("Configuration is ") ver + cc.j(joConfiguration) + "\n" );
    var strConfigurationFilePath = strNodeFolder + path.sep + "config.json";
    fs.writeFileSync( strConfigurationFilePath, JSON.stringify( joConfiguration, null, 4 ) );
    log.write( strNSP + cc.debug( "Configuration saved to " ) + cc.note( strConfigurationFilePath ) + "\n" );
    //
    // create clean.sh, run-proxy.sh, run-skaled.sh, shutdown-skaled.sh
    //
    log.write( strNSP + cc.debug( "Creating scripts for node " ) + cc.bright( idxNode ) + cc.debug( "..." ) + "\n" );
    copy_template_file( idxNode, "clean.sh", strNodeFolder );
    //copy_template_file( idxNode, "run-proxy.sh", strNodeFolder );
    copy_template_file( idxNode, "run-skaled.sh", strNodeFolder );
    copy_template_file( idxNode, "shutdown-skaled.sh", strNodeFolder );
    //
    log.write( strNSP + cc.debug( "Running final dynamic init scripts for node " ) + cc.bright( idxNode ) + cc.debug( "..." ) + "\n" );
    child_process.execSync( strNodeFolder + path.sep + "clean.sh" );
    log.write( cc.success( "Done" ) + cc.debug( ", node " ) + cc.bright( idxNode ) + cc.debug( " initialized" ) + "\n" );
    strContentRunSH += "/bin/bash " + strNodeFolder + path.sep + "run-skaled.sh SKALED=$SKALED &>/dev/null &\n";
    //strContentRunSH += "/bin/bash " + strNodeFolder + path.sep + "run-proxy.sh &>/dev/null &\n";
    strContentShutdownSH += "/bin/bash " + strNodeFolder + path.sep + "shutdown-skaled.sh &>/dev/null &\n";
}
//
let strRunSH = __dirname + path.sep + "run.sh";
log.write( cc.debug( "Creating " ) + cc.info( strRunSH ) + cc.debug( " script..." ) + "\n" );
fs.writeFileSync( strRunSH, strContentRunSH );
fs.chmodSync( strRunSH, 0777 );
log.write( cc.success( "Done" ) + cc.debug( ", " ) + cc.info( strRunSH ) + cc.debug( " script created" ) + "\n" );
//
let strShutdownSH = __dirname + path.sep + "shutdown.sh";
log.write( cc.debug( "Creating " ) + cc.info( strShutdownSH ) + cc.debug( " script..." ) + "\n" );
fs.writeFileSync( strShutdownSH, strContentShutdownSH );
fs.chmodSync( strShutdownSH, 0777 );
log.write( cc.success( "Done" ) + cc.debug( ", " ) + cc.info( strShutdownSH ) + cc.debug( " script created" ) + "\n" );
